import React from "react";
import Link from "next/link";
import Svg from "./Svg";
import SvgPhone from "./SvgPhone";

export default function Home() {
  const MenuHandler = (flag) => {
    if (flag) {
      document.getElementById("list").classList.add("top-100");
      document.getElementById("list").classList.remove("hidden");
      document.getElementById("close").classList.remove("hidden");
      document.getElementById("open").classList.add("hidden");
    } else {
      document.getElementById("list").classList.remove("top-100");
      document.getElementById("list").classList.add("hidden");
      document.getElementById("close").classList.add("hidden");
      document.getElementById("open").classList.remove("hidden");
    }
  };

  // menu components

  const Comp = ({ isMobile }) => {
    const menu = [
      {
        title: "Home",
        href: "/",
      },
      {
        title: "About",
        href: "/about",
      },
      {
        title: "Contact",
        href: "/contact",
      },
    ];

    // return (
    //     <ul>
    // {menu.map(item => (

    //     <Link key={item.title} className={"mr-20 cursor-pointer"} href={item.href}>{item.title}</Link>

    // ))}

    // </ul>
    // )
  };

  return (
    <>
      <div className="mx-auto container px-2 lg:px-0">
        <nav className="hidden lg:block">
          <div className="flex justify-between items-center py-10 lg:pt-20">
            <div>
              <Svg></Svg>
            </div>
            <div className="flex items-center text-base font-normal text-gray-800">
              {/* <Link href="/">
                <div className="mr-20 cursor-pointer">Home</div>
              </Link>

              <Link href="/about">
                <div className="mr-20 cursor-pointer">About</div>
              </Link>
              <Link href="/services">
                <div className="mr-20 cursor-pointer">Services</div>
              </Link>

              <Link href="/contact">
                <div className="cursor-pointer">Contact</div>
              </Link> */}

              {menu.map((item) => (
                <Link
                  key={item.title}
                  className={"mr-20 cursor-pointer"}
                  href={item.href}
                >
                  {item.title}
                </Link>
              ))}
            </div>
          </div>
        </nav>
        {/* Mobile Responsive */}
        <nav className="lg:hidden">
          <div className="flex py-2 justify-between items-center px-4">
            <div className="cursor-pointer">
              <a href="#">
                <SvgPhone></SvgPhone>
              </a>
            </div>
            <div className="visible flex items-center">
              <ul
                id="list"
                className="hidden p-2 border-r bg-white absolute rounded top-0 left-0 right-0 shadow mt-12 md:mt-12 z-40"
              >
                <li className="flex cursor-pointer text-gray-600 text-sm leading-3 tracking-normal mt-2 py-2 hover:text-indigo-700 focus:text-indigo-700 focus:outline-none">
                  <a href="#">
                    <span className="ml-2 font-bold">Home</span>
                  </a>
                </li>
                <li
                  className="flex flex-col cursor-pointer text-gray-600 text-sm leading-3 tracking-normal py-2 hover:text-indigo-700 focus:text-indigo-700 focus:outline-none flex justify-center"
                  onclick="dropdownHandler(this)"
                >
                  <Link href="/about">
                    <span className="ml-2 font-bold">About</span>
                  </Link>
                </li>
                <li className="flex cursor-pointer text-gray-600 text-sm leading-3 tracking-normal py-2 hover:text-indigo-700 flex items-center focus:text-indigo-700 focus:outline-none">
                  <a href="#">
                    <span className="ml-2 font-bold">Services</span>
                  </a>
                </li>
                <li
                  className="flex flex-col cursor-pointer text-gray-600 text-sm leading-3 tracking-normal py-2 hover:text-indigo-700 focus:text-indigo-700 focus:outline-none flex justify-center"
                  onclick="dropdownHandler(this)"
                >
                  <a href="#">
                    <span className="ml-2 font-bold">Collections</span>
                  </a>
                </li>
              </ul>
              <div className="xl:hidden">
                <svg
                  id="open"
                  onClick={() => MenuHandler(true)}
                  aria-haspopup="true"
                  aria-label="Main Menu"
                  xmlns="http://www.w3.org/2000/svg"
                  className="show-m-menu icon icon-tabler icon-tabler-menu"
                  width={28}
                  height={28}
                  viewBox="0 0 24 24"
                  strokeWidth="1.5"
                  stroke="currentColor"
                  fill="none"
                  strokeLinecap="round"
                  strokeLinejoin="round"
                >
                  <path stroke="none" d="M0 0h24v24H0z" />
                  <line x1={4} y1={8} x2={20} y2={8} />
                  <line x1={4} y1={16} x2={20} y2={16} />
                </svg>
                <div
                  id="close"
                  className="hidden close-m-menu"
                  onClick={() => MenuHandler(false)}
                >
                  <svg
                    aria-label="Close"
                    xmlns="http://www.w3.org/2000/svg"
                    width={24}
                    height={24}
                    viewBox="0 0 24 24"
                    strokeWidth="1.5"
                    stroke="currentColor"
                    fill="none"
                    strokeLinecap="round"
                    strokeLinejoin="round"
                  >
                    <path stroke="none" d="M0 0h24v24H0z" />
                    <line x1={18} y1={6} x2={6} y2={18} />
                    <line x1={6} y1={6} x2={18} y2={18} />
                  </svg>
                </div>
              </div>
            </div>
          </div>
        </nav>
      </div>
      {/* Mobile Responsive */}
      <style>
        {`.text-color-para {
                color: #636363;
            }
            .text-color-heading {
                color: #334048;
            }

            .background-color {
                background: #334048;
            }
            .btn-bg {
                background-color: #202b32;
            }
            .top-100 {
                animation: slideDown 0.5s ease-in-out;
            }
            @keyframes slideDown {
                0% {
                    top: -50%;
                }
                100% {
                    top: 0;
                }
            }
            * {
                outline: none !important;
                -webkit-tap-highlight-color: rgba(0, 0, 0, 0);
                -webkit-tap-highlight-color: transparent;
            }.f-f-l {
                font-family: "Roboto", sans-serif;
            }
            .f-f-d-s {
                font-family: "DM Serif Display", serif;
            }
            .font-lato {
                font-family: "Lato", sans-serif;
            }`}
      </style>
    </>
  );
}
